  private void search_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_buttonActionPerformed
    // TODO add your handling code here:
    String user_name = user_name_field.getText();
    if (user_name_field.equals("")) {
      JOptionPane.showMessageDialog(null, "Enter user name to search");
      showLogsTable(null);
      return;
    }
    List<String> logs_list = ((Admin) admin).getRecordLogs(user_name);
    if (logs_list.isEmpty()) {
      JOptionPane.showMessageDialog(null, "Cannot find recorded log");
      showLogsTable(null);
    } else {
      showLogsTable(logs_list);
    }
  }//GEN-LAST:event_search_buttonActionPerformed

  private void showLogsTable(List<String> logs_list) {    
    DefaultTableModel model = (DefaultTableModel) logs_table.getModel();
    String[] col_name = {"User name", "Log type", "Date", "Time"};
    if (logs_list == null || logs_list.isEmpty()) {
      model.setRowCount(0);
    } else {
      String[][] data = new String[logs_list.size()][4];
      int row_count = 0;
      for (String log: logs_list) {
        data[row_count] = log.split("!");
        row_count++;
      }
      model.setDataVector(data, col_name);
    }
  }
  
