  private void manager_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_manager_buttonActionPerformed
    // TODO add your handling code here:
    List<String> manager_logs = ((Admin) admin).getRecordLogs("Manager");
    if (manager_logs.isEmpty()) {
      JOptionPane.showMessageDialog(null, "No manager recorded logs");
    }
    showLogsTable(manager_logs);
  }//GEN-LAST:event_manager_buttonActionPerformed

