  private void search_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_buttonActionPerformed
    // TODO add your handling code here:
    String id = id_field.getText();
    if (id.equals("")) {
      JOptionPane.showMessageDialog(null, "Enter tender ID to search");
    } else {
      try {
        Integer.parseInt(id);
      } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(null, "ID is not number. Enter ID as number");
        return;
      }
      if (Integer.parseInt(id) < 0) {
        JOptionPane.showMessageDialog(null, "Invalid, negative ID");
        return;
      }
    }
    List<String> found_tender = ((Manager) manager).searchTender(id_field.getText());
    if (found_tender.size() == 0) {
      JOptionPane.showMessageDialog(null, "Cannot find tender ID: "+id_field.getText());
      return;
    }
    showTenderTable(found_tender);
  }//GEN-LAST:event_search_buttonActionPerformed

  private void showTenderTable (List<String> tender_list) {
    DefaultTableModel model = (DefaultTableModel) products_table.getModel();
    String[] col_names = {"ID", "Tender Name", "Tender Price (RM)", "Products Price (RM)", "Tender Discount (RM)", "Labour Cost (RM)", "Mark-up (RM)", "Deadline (dd/mm/yyyy)", "Status", "Create Date (dd/mm/yyyy)", "Created Time"};          
    if (tender_list.isEmpty()) {
      String[][] matrix_data = new String[0][11];
      model.setDataVector(matrix_data, col_names);
    } else {
      String[][] matrix_data = new String[tender_list.size()][11];
      int row_count = 0;
      for (String tender: tender_list) {
        matrix_data[row_count] = tender.split("!");
        row_count++;
      }
      model.setDataVector(matrix_data, col_names);
    }
  }

  private void show_all_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_show_all_buttonActionPerformed
    // TODO add your handling code here:
    List<String> tender_list = ((Manager) manager).searchTender("all");
    if (tender_list.isEmpty()) {
      JOptionPane.showMessageDialog(null, "There is no tendering request. Manager can add new tendering request");
    }
    showTenderTable(tender_list);
  }//GEN-LAST:event_show_all_buttonActionPerformed

