  private void delete_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_delete_buttonActionPerformed
    // TODO add your handling code here:
    DefaultTableModel model = (DefaultTableModel) products_table.getModel();
    if (products_table.getSelectedRow() == -1) {
      if (products_table.getRowCount() == 0) {
        JOptionPane.showMessageDialog(null, "There is no tendering request in the table");
      } else {
        JOptionPane.showMessageDialog(null, "Select a tendering request in the table to delete");
      }      
    } else {
      int dialog_button = JOptionPane.YES_NO_OPTION;
      int dialog_result = JOptionPane.showConfirmDialog(null, "Do you want to delete this tender?","Delete confirmation",dialog_button);
      if (dialog_result == 0) {
        String delete_id = products_table.getValueAt(products_table.getSelectedRow(),0)+"";
        model.removeRow(products_table.getSelectedRow());
        Tender tender = new Tender(Integer.parseInt(delete_id));
        ((Manager) manager).deleteTender(tender);
      } 
    }
  }//GEN-LAST:event_delete_buttonActionPerformed

