  private void search_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_buttonActionPerformed
    // TODO add your handling code here:
    String id = id_field.getText();
    if (id.equals("")) {
      JOptionPane.showMessageDialog(null, "Enter product ID to search");
      return;
    } else {
      try {
        if (Integer.parseInt(id) <= 0) {
          JOptionPane.showMessageDialog(null, "Invalid ID");
          id_label.setText("");
          brand_field.setText("");
          model_field.setText("");
          type_field.setText("");
          price_field.setText("");
          return;
        }
      } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(null, "Invalid ID");
        id_label.setText("");
        brand_field.setText("");
        model_field.setText("");
        type_field.setText("");
        price_field.setText("");
        return;
      }
    }
    Product product = ((Manager) manager).findProduct(id_field.getText());
    if (product == null) {
      JOptionPane.showMessageDialog(null, "Cannot find product");
      id_label.setText("");
      brand_field.setText("");
      model_field.setText("");
      type_field.setText("");
      price_field.setText("");
      return;
    }
    id_label.setText(id_field.getText());
    brand_field.setText(product.getBrand());
    model_field.setText(product.getModel());
    type_field.setText(product.getType());
    price_field.setText(product.getPrice()+"");
  }//GEN-LAST:event_search_buttonActionPerformed

