  private void save_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_save_buttonActionPerformed
    // TODO add your handling code here:
    String product_id, new_brand, new_model, new_type, new_price;
    product_id = id_label.getText();
    if (product_id.equals("")) {
      JOptionPane.showMessageDialog(null, "No product ID to edit");
      return;
    } 
    new_brand = brand_field.getText();
    if (new_brand.equals("")) {
      JOptionPane.showMessageDialog(null, "No brand");
      return;
    }
    new_model = model_field.getText();
    if (new_model.equals("")) {
      JOptionPane.showMessageDialog(null, "No model");
      return;
    }
    new_type = type_field.getText();
    if (new_type.equals("")) {
      JOptionPane.showMessageDialog(null, "No type");
      return;
    }
    new_price = price_field.getText();
    if (new_price.equals("")) {
      JOptionPane.showMessageDialog(null, "No price");
      return;
    }
    try {
      if (Float.parseFloat(new_price) < 0) {
        JOptionPane.showMessageDialog(null, "Invalid price");
        return;
      }
    } catch (NumberFormatException e) {
      JOptionPane.showMessageDialog(null, "Invalid price");
      return;
    }
    Product product = ((Manager) manager).findProduct(id_field.getText());
    if (product.checkDuplicateProduct(new_brand, new_model, new_type)) {
      JOptionPane.showMessageDialog(null, "Duplicated product");
      return;
    }
    if (product.getBrand().equals(new_brand) & product.getModel().equals(new_model) &
        product.getType().equals(new_type) & product.getPrice() == Float.parseFloat(new_price)) {
      JOptionPane.showMessageDialog(null, "There is no change in the current product");
      return;
    }
    int dialog_button = JOptionPane.YES_NO_OPTION;
    int dialog_result = JOptionPane.showConfirmDialog(null, "Do you want to edit the product?","Edit confirmation",dialog_button);
    if (dialog_result == 0) {
      product.setBrand(new_brand);
      product.setType(new_type);
      product.setModel(new_model);
      product.setPrice(Float.parseFloat(new_price));
      ((Manager) manager).editProduct(product);
      JOptionPane.showMessageDialog(null, "Product id: "+product_id+" is editted");
    } else {
      JOptionPane.showMessageDialog(null, "Product id: "+product_id+" is not editted");
    }
  }//GEN-LAST:event_save_buttonActionPerformed

